async function searchFlashcards() {
  const query = document.getElementById('search-input').value.trim();
  const deckId = document.getElementById('deck-filter').value;
  const difficulty = document.getElementById('difficulty-filter').value;
  const sortBy = document.getElementById('sort-by').value;

  try {
    const params = new URLSearchParams();
    if (query) params.append('q', query);
    if (deckId) params.append('deck_id', deckId);
    if (difficulty) params.append('difficulty', difficulty);
    if (sortBy) params.append('sort_by', sortBy);

    const response = await fetch(`${API_BASE}/api/flashcards/search?${params}`);
    const flashcards = await response.json();

    displaySearchResults(flashcards);

  } catch (error) {
    console.error('Error searching flashcards:', error);
    showNotification('Error al buscar flashcards', 'error');
  }
}

