async function editDeck(deckId) {
  try {
    const response = await fetch(`${API_BASE}/api/decks/${deckId}`);
    const deck = await response.json();

    editingDeckId = deckId;

    document.getElementById('edit-deck-name').value = deck.name;
    document.getElementById('edit-deck-description').value = deck.description || '';
    document.getElementById('edit-deck-public').checked = deck.is_public;

    showEditDeckModal();

  } catch (error) {
    console.error('Error loading deck for edit:', error);
    showNotification('Error al cargar deck para editar', 'error');
  }
}

async function updateDeck() {
  if (!editingDeckId) return;

  const name = document.getElementById('edit-deck-name').value.trim();
  const description = document.getElementById('edit-deck-description').value.trim();
  const isPublic = document.getElementById('edit-deck-public').checked;

  if (!name) {
    showNotification('Por favor, ingresa un nombre para el deck', 'error');
    return;
  }

  try {
    const response = await fetch(`${API_BASE}/api/decks/${editingDeckId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('authToken')}`
      },
      body: JSON.stringify({
        name,
        description,
        is_public: isPublic
      })
    });

    const data = await response.json();

    if (response.ok) {
      showNotification('Deck actualizado exitosamente', 'success');
      hideEditDeckModal();
      loadManageDecks();
    } else {
      showNotification(data.error || 'Error al actualizar deck', 'error');
