  const files = event.dataTransfer.files;
  if (files.length > 0) {
    processImportFile(files[0]);
  }
}

function processImportFile(file) {
  const reader = new FileReader();

  reader.onload = function(e) {
    const content = e.target.result;
    const extension = file.name.split('.').pop().toLowerCase();

    // Store file content for import
    window.importFileContent = content;
    window.importFileFormat = extension;

    // Update UI to show file is ready
    const uploadArea = document.getElementById('file-upload-area');
    uploadArea.innerHTML = `
      <div class="upload-icon">‚úÖ</div>
      <div class="upload-text">Archivo cargado: ${file.name}</div>
      <div class="upload-hint">Listo para importar</div>
    `;

    showNotification('Archivo cargado correctamente', 'success');
  };

  reader.readAsText(file);
}

