    e.preventDefault();
    uploadArea.style.borderColor = '#cbd5e0';
    uploadArea.style.background = '#f8fafc';
});

uploadArea.addEventListener('drop', (e) => {
    e.preventDefault();
    uploadArea.style.borderColor = '#cbd5e0';
    uploadArea.style.background = '#f8fafc';

    const files = Array.from(e.dataTransfer.files);
    handleFiles(files);
});

// Configurar input de archivo
document.getElementById('fileInput').addEventListener('change', (e) => {
    const files = Array.from(e.target.files);
    handleFiles(files);
});

// Manejar archivos subidos
function handleFiles(files) {
    const validFiles = files.filter(file => {
        const validTypes = ['image/png', 'image/jpeg', 'image/jpg', 'image/svg+xml'];
        const maxSize = 2 * 1024 * 1024; // 2MB

        if (!validTypes.includes(file.type)) {
            showStatus(`‚ùå ${file.name}: Formato no v√°lido`, 'error');
            return false;
        }

        if (file.size > maxSize) {
            showStatus(`‚ùå ${file.name}: Archivo muy grande (m√°x. 2MB)`, 'error');
            return false;
        }

        return true;
    });

    if (validFiles.length === 0) return;

    validFiles.forEach(file => {
        const reader = new FileReader();
        reader.onload = (e) => {
            const iconData = {
                name: file.name.replace(/\.[^/.]+$/, ''),
                filename: file.name,
                data: e.target.result,
                type: file.type.includes('svg') ? 'svg' : 'image'
            };

            uploadedIcons.push(iconData);
            updateIconPreview();
            showStatus(`‚úÖ ${file.name} subido exitosamente`, 'success');
        };
        reader.readAsDataURL(file);
    });
}

// Actualizar vista previa
function updateIconPreview() {
    const preview = document.getElementById('iconPreview');
    preview.innerHTML = '';

    uploadedIcons.forEach((icon, index) => {
        const iconItem = document.createElement('div');
        iconItem.className = 'icon-item';
        iconItem.innerHTML = `
            <div class="icon-display">
                <img src="${icon.data}" alt="${icon.name}">
            </div>
            <div class="icon-name">${icon.name}</div>
            <div class="icon-actions">
                <button class="btn-small btn-copy" onclick="copyIconCode('${icon.name}')">
                    Copiar C√≥digo
                </button>
                <button class="btn-small btn-delete" onclick="removeIcon(${index})">
                    Eliminar
                </button>
            </div>
        `;
        preview.appendChild(iconItem);
    });
}

// Copiar c√≥digo del icono
window.copyIconCode = function(iconName) {
    const code = `IconManager.renderIcon('${iconName}')`;
    navigator.clipboard.writeText(code).then(() => {
        showStatus(`üìã C√≥digo copiado: ${code}`, 'info');
    });
};

// Eliminar icono
window.removeIcon = function(index) {
    uploadedIcons.splice(index, 1);
    updateIconPreview();
    showStatus('üóëÔ∏è Icono eliminado', 'info');
};

// Generar icono con IA
window.generateIcon = async function() {
    const description = document.getElementById('iconDescription').value;
    const style = document.getElementById('iconStyle').value;

    if (!description.trim()) {
        showStatus('‚ùå Por favor describe el icono que quieres generar', 'error');
        return;
    }

    showStatus('ü§ñ Generando icono con IA...', 'info');

    try {
        const result = await IconManager.generateIcon(description, style);

        if (result.success) {
            const iconData = {
                name: result.name,
                filename: `${result.name}.svg`,
                data: `data:image/svg+xml;base64,${btoa(result.svg)}`,
                type: 'svg'
            };

            uploadedIcons.push(iconData);
            updateIconPreview();
            showStatus(`‚úÖ Icono "${description}" generado exitosamente`, 'success');
        } else {
            showStatus(`‚ö†Ô∏è ${result.message}`, 'error');
        }
    } catch (error) {
        showStatus(`‚ùå Error generando icono: ${error.message}`, 'error');
    }
};

// Mostrar mensaje de estado
function showStatus(message, type) {
    const statusDiv = document.getElementById('statusMessage');
    statusDiv.className = `status-message status-${type}`;
    statusDiv.textContent = message;

    setTimeout(() => {
        statusDiv.textContent = '';
        statusDiv.className = '';
    }, 5000);
}

// Guardar iconos al cerrar
window.addEventListener('beforeunload', () => {
    if (uploadedIcons.length > 0) {
        // Aqu√≠ se guardar√≠an los iconos en el sistema
        console.log('üíæ Guardando iconos...', uploadedIcons);
    }
});

